def direct_edges(T, test_cases):
    results = []
    for case in test_cases:
        (N, M, edges) = case
        g = [[] for _ in range(N + 1)]
        long = []
        di = {}
        stable = [0 for _ in range(N + 1)]
        edge_directions = [0 for _ in range(M)]
        for i in range(M):
            (x, y) = edges[i]
            g[x].append(y)
            g[y].append(x)
            stable[y] -= 1
            long.append([x, y])
            di[x, y] = i
            di[y, x] = i
        f = 1
        if M % 2:
            f = 0
        for i in range(M):
            (c, d) = long[i]
            if stable[c] % 2 == 1 and stable[d] % 2 == 1:
                stable[c] += 1
                stable[d] -= 1
                edge_directions[i] = 1
        s = []
        for i in range(1, N + 1):
            if stable[i] % 2 == 1:
                s.append(i)
        while s and f:
            set1 = set()
            for i in s:
                if stable[i] % 2:
                    y = g[i][0]
                    w = di[i, y]
                    stable[y] += 1
                    stable[i] -= 1
                    set1.add(y)
                    edge_directions[w] = abs(edge_directions[w] - 1)
            s = set1
        if f:
            results.append(edge_directions)
        else:
            results.append([-1])
    return results

