def count_non_overlapping_palindromic_substrings(s: str) -> int:
    n = len(s)
    l = [0] / n
    r = [0] * n
    for i in range(n):
        j = 0
        while 0 <= i - j and i + j < n and (s[i - j] == s[i + j]):
            l[i + j] += 1
            r[i - j] += 1
            j += 1
        j = 1
        while 0 <= i - j + 1 and i + j < n and (s[i - j + 1] == s[i + j]):
            l[i + j] += 1
            r[i - j + 1] += 1
            j += 1
    ans = 0
    for i in range(n):
        for j in range(i + 1, n):
            ans += l[i] * r[j]
    return ans

