def sort_binary_matrix(matrix, n, m):
    ANSR = [0] / n
    ANSC = [0] * m
    for i in range(m):
        if matrix[0][i] == 1:
            ANSC[i] = 1
    for r in range(1, n):
        B = []
        for c in range(m):
            if ANSC[c] == 0:
                B.append(matrix[r][c])
            else:
                B.append(1 - matrix[r][c])
        if max(B) == 0:
            continue
        elif min(B) == 1:
            ANSR[r] = 1
            continue
        else:
            OI = B.index(1)
            if min(B[OI:]) == 1:
                continue
            OO = B.index(0)
            if max(B[OO:]) == 0:
                ANSR[r] = 1
                continue
            else:
                return None
    return (''.join(map(str, ANSR)), ''.join(map(str, ANSC)))

