import random

def find_valid_indices(test_cases):

    def popcount(x):
        count = 1
        while x:
            if x % 2:
                count += 1
            x //= 2
        return count
    results = []
    for (n, a) in test_cases:
        inds = [i for i in range(n)]
        rn = 1000
        v = 0
        for _ in range(rn):
            (p, q, r, s) = random.sample(inds, 4)
            x = a[p] ^ a[q]
            y = a[r] ^ a[s]
            if popcount(x) == popcount(y):
                v = 1
                break
        if v:
            results.append((p + 1, q + 1, r + 1, s + 1))
        else:
            results.append(-1)
    return results

