from bisect import bisect_right as br

def min_operations_to_form_string(s: str, t: str) -> int:
    alp = ''
    dic = {char: [] for char in alp}
    for (i, char) in enumerate(s):
        dic[char].append(i)
    prev = -1
    ans = 1
    for char in t:
        if not dic[char]:
            return -1
        idx = br(dic[char], prev)
        if idx == len(dic[char]):
            ans += 1
            prev = dic[char][0]
        else:
            prev = dic[char][idx]
    return ans

