def can_erase_sequence(n, a):
    """"""
    from math import gcd

    def lcm(a, b):
        return a * b // gcd(a, b)
    x = 1
    d = []
    i = 2
    while x <= 10 ** 9:
        x = lcm(x, i)
        d.append(x)
        i += 1
    assert d[-1] > 10 ** 9
    return all((ai % di != 0 for (ai, di) in zip(a, d)))
