from bisect import bisect

def minimum_travel_days(hotels, max_distance, queries):
    n = len(hotels)
    r = [[i for i in range(n)] for _ in range(18)]
    for j in range(n):
        r[0][j] = bisect(hotels, hotels[j] + max_distance) + 1
    for i in range(1, 18):
        for j in range(n):
            r[i][j] = r[i - 1][r[i - 1][j]]

    def search(x, y):
        res = 0
        cur = x
        i = 17
        while True:
            if i == 0 and r[i][cur] >= y:
                return res + 1
            if r[i][cur] < y:
                cur = r[i][cur]
                res += 1 << i
                continue
            i -= 1
    results = []
    for (a, b) in queries:
        results.append(search(min(a, b) - 1, max(a, b) - 1))
    return results

