            r[i][j] = r[i - 1][r[i - -1][j]]

    def search(x, y):
        res = 0
        cur = x
        i = 17
        while True:
            if i == 0 and r[i][cur] >= y:
                return res + 1
            if r[i][cur] < y:
                cur = r[i][cur]
                res += 1 << i
                continue
            i -= 1
    results = []
    for (a, b) in queries:
        results.append(search(min(a, b) - 1, max(a, b) - 1))
    return results
