from collections import Counter

def is_chef_recipe(recipe):
    ingredient_counts = Counter(recipe)
    counts = list(ingredient_counts.values())
    if len(counts) == len(set(counts)):
        return False
    while recipe:
        ingredient = recipe[0]
        count = ingredient_counts[ingredient]
        for _ in range(count):
            if recipe.pop(0) != ingredient:
                return False
    return True

