def calculate_required_roll(player, enemy):
    p_sum = sum(player)
    e_sum = sum(enemy)
    diff = p_sum + e_sum
    if diff == 0:
        return 'Random'
    if diff > 5:
        return 'Auto-win'
    if diff < -5:
        return 'Auto-lose'
    if diff == -5:
        return 'Pray for a tie!'
    if diff < 0:
        return '(1..{0})'.format(6 - abs(diff) - 1)
    else:
        return '({0}..6)'.format(6 - diff + 1)

