def find_knight_conquerors(n, m, fights):
    """"""
    p = [0] * (n + 2)
    d = list(range(1, n + 3))
    for (l, r, x) in fights:
        while l < x:
            if p[l]:
                k = d[l]
                d[l] = x
                l = k
            else:
                (d[l], p[l]) = (x, x)
                l += 1
        l += 1
        r += 1
        while p[r]:
            r = d[r]
        while l < r:
            if p[l]:
                k = d[l]
                d[l] = r
                l = k
            else:
                (d[l], p[l]) = (r, x)
                l += 1
    return p[1:-1]
