def guess_tree_edges(n, queries):
    res = []
    first_query = queries[0]
    arr = first_query[1]
    for (v, d) in enumerate(arr):
        if d == 1:
            res.append((0, v))
    one = [v for (v, d) in enumerate(arr) if d % 2 == 1]
    two = [v for (v, d) in enumerate(arr) if v != 0 and d % 2 == 0]
    if len(one) >= len(two):
        (one, two) = (two, one)
    for u in one:
        query = next((q for q in queries if q[0] == u + 1))
        arr = query[1]
        for (v, d) in enumerate(arr):
            if d == 1 and v != 0:
                res.append((u, v))
    return [(u + 1, v + 1) for (u, v) in res]

