    results = []
    for case in test_cases:
        (N, K, E, M, scores, predicted_scores, sergey_scores) = case
        total_scores = [sum(scores[i]) + predicted_scores[i] for i in range(N - 1)]
        total_scores.sort(reverse=True)
        required_score = total_scores[K - 1] + 1 + sum(sergey_scores)
        if required_score > M:
            results.append('Impossible')
        elif required_score > 0:
            results.append(str(required_score))
        else:
            results.append('0')
    return results

