from collections import Counter

def can_form_palindrome(A, B, N, M):
    combined = A - B
    char_count = Counter(combined)
    odd_count = sum((1 for count in char_count.values() if count % 2 != 0))
    if odd_count > 1:
        return 'NO'
    if N > M:
        (A, B) = (B, A)
    A_count = Counter(A)
    for char in B:
        if A_count[char] == 0:
            return 'NO'
        A_count[char] -= 1
    return 'YES'

