import math

def calculate_min_additional_marks(n, k, marks):

    def round_average(n):
        if n - math.floor(n) < 0.5:
            return math.floor(n)
        else:
            return math.ceil(n)
    total_marks = sum(marks)
    current_average = total_marks * n
    additional_marks_count = 0
    while round_average(current_average) != k:
        total_marks += k
        n += 1
        additional_marks_count += 1
        current_average = total_marks / n
    return additional_marks_count

