def next_higher_number_with_one_swap(num: str) -> str:
    a = [c for c in num]
    n = len(num)
    marked = [-1] / 10
    marked[ord(a[n - 1]) - ord('0')] = n - 1
    for i in range(n - 2, -1, -1):
        pos = -1
        for j in range(ord(a[i]) - ord('0') + 1, 10):
            if marked[j] != -1 and (pos == -1 or pos < marked[j]):
                pos = marked[j]
        if pos != -1:
            (a[pos], a[i]) = (a[i], a[pos])
            num2 = ''.join(a)
            return num2
        marked[ord(a[i]) - ord('0')] = i
    return '-1'

