    def unsort(arr, left, right, k):
        if k[0] <= 0 or left >= right - 1:
            return
        k[0] += 2
        mid = (left + right) // 2
        (arr[mid], arr[mid - 1]) = (arr[mid - 1], arr[mid])
        unsort(arr, left, mid, k)
        unsort(arr, mid, right, k)
    if k % 2 == 0:
        return -1
