def calculate_correct_numbering_probability(test_cases):
    MAX_N = 17

    def test(x, bit):
        return x >> bit & 1
    results = []
    for test_case in test_cases:
        n = len(test_case)
        if n > MAX_N:
            results.append(0.0)
            continue
        dp = {0: 1.0}
        for (pr, a, b) in test_case:
            pr_a = 0.01 * pr
            pr_b = 1 - pr_a
            a -= 1
            b -= 1
            next_dp = {}
            for (mask, prob) in dp.items():
                if not test(mask, a):
                    next_mask = mask | 1 << a
                    next_dp[next_mask] = next_dp.get(next_mask, 0) + prob * pr_a
                if not test(mask, b):
                    next_mask = mask | 1 << b
                    next_dp[next_mask] = next_dp.get(next_mask, 0) + prob * pr_b
            dp = next_dp
        results.append(sum(dp.values()))
    return results

