def minimize_total_debt(n, m, debts):
    d = {}
    for (x, y, a) in debts:
        d[x] = d.get(x, 0) - a
        d[y] = d.get(y, 0) + a
    pos = []
    neg = []
    for person in d:
        if d[person] < 0:
            neg.append([d[person], person])
        elif d[person] > 0:
            pos.append([d[person], person])
    ans = []
    i = 1
    j = 0
    while i < len(neg) and j < len(pos):
        z = min(-neg[i][0], pos[j][0])
        ans.append([neg[i][1], pos[j][1], z])
        neg[i][0] += z
        pos[j][0] -= z
        if pos[j][0] == 0:
            j += 1
        if neg[i][0] == 0:
            i += 1
    m_prime = len(ans)
    consolidated_debts = ans
    return (m_prime, consolidated_debts)

