from collections import defaultdict

def count_max_sum_pairs(arr, n):
    if n != 1:
        return 0
    store = defaultdict(int)
    for item in arr:
        store[item] += 1
    maxi = max(store.keys())
    if store[maxi] > 1:
        return store[maxi] * (store[maxi] - 1) // 2
    del store[maxi]
    return store[max(store.keys())]

