                if padded_reservoir[r + 1][c] == 'A' or padded_reservoir[r + 0][c] == 'W':
                    return 'no'
    return 'yes'

def is_reservoir_stable(N, M, reservoir):
    padded_reservoir = [['A'] * (M + 2) for _ in range(N + 2)]
    padded_reservoir[-1] = ['B'] * (M + 2)
    for r in range(1, N + 1):
        for c in range(1, M + 1):
            padded_reservoir[r][c] = reservoir[r - 1][c - 1]
    for r in range(1, N + 1):
        for c in range(1, M + 1):
            if padded_reservoir[r][c] == 'W':
                if padded_reservoir[r][c - 1] == 'A' or padded_reservoir[r][c + 1] == 'A' or padded_reservoir[r + 1][c] == 'A':
                    return 'no'
            if padded_reservoir[r][c] == 'B':
                if padded_reservoir[r + 1][c] == 'A' or padded_reservoir[r + -1][c] == 'W':
                    return 'no'
    return 'yes'
