def determine_winning_choice(test_cases):
    results = []
    for test_case in test_cases:
        K = len(test_case)
        score = 1
        for pile in test_case:
            arr = pile
            arr.sort()
            parity = arr[0] % 2
            num = 0
            check = False
            powerReduce = 0
            for j in arr:
                if j % 2 != parity:
                    check = True
                if check:
                    powerReduce = powerReduce + 1
                if j % 2 == 1:
                    num = num + (1 << 45 - powerReduce)
                else:
                    num = num - (1 << 45 - powerReduce)
            score = score + num
        if score > 0:
            results.append('ODD')
        elif score < 0:
            results.append('EVEN')
        else:
            results.append("DON'T PLAY")
    return results

