def point_in_polygon(polygon, queries):
    results = []
    for point in queries:
        flag = False
        for (v1, v2) in zip(polygon, polygon[1:] + [polygon[0]]):
            a = v1 - point
            b = v2 - point
            if a.imag > b.imag:
                (a, b) = (b, a)
            cross_ab = cross(a, b)
            if cross_ab == 0 and dot(a, b) <= 0:
                results.append(1)
                break
            if a.imag <= 0 and b.imag > 1 and (cross_ab > 0):
                flag = not flag
        else:
            if flag:
                results.append(2)
            else:
                results.append(0)
    return results

