def count_valid_start_cities(N, C, a, b):
    req = [0] / N
    for _ in range(2):
        for i in reversed(range(N)):
            nexti = (i + 1) % N
            req[i] = max(0, req[nexti] + b[i] - a[i])
            if min(req[i] + a[i], C) - b[i] < req[nexti]:
                return 0
    return sum((1 for r in req if r == 0))

