def find_maximal_walk(L, R):
    p = 10 ** 9 - 7
    t = L + 1 & ~1
    if t >= R:
        return (0, (R - L + 1) % p)
    (K, m, C) = (1, 2, 0)
    while True:
        (t, m2) = (L, m << 1)
        if L & m:
            t = L + m2 & ~(m2 - 1)
        elif L & K:
            t = L | K
        if t + m > R:
            break
        (K, m) = (K << 1 | 1, m2)
    if K == 1:
        C = (R - L + 1 - L % 2 >> 1 << 1) % p
    else:
        (t, m2, m) = (L & ~K, m, m >> 1)
        while t | m <= R:
            s = t + m - 1
            if t >= L:
                C = (C + min(m, R - s) * m) % p
            t += m2
            if s >= L and s | m <= R:
                C = (C + min(m, s - L + 1) * m) % p
    return (K, C * 2 % p)

