def guess_hidden_number(hidden_number, max_value, max_queries):
    max_k = 10001
    max_shift = [max_k]
    for t in range(1, 5):
        max_shift.append(max_shift[t - 1] * (max_k + 1) + max_k)

    def get_right(left, t):
        if left >= max_k:
            return left + max_shift[t - 1] - 1
        k = left
        if k > max_k:
            k = max_k
        if t == 1:
            return left + k - 1
        new_left = left
        for i in range(k):
            new_left = get_right(new_left, t - 1) + 2
        return get_right(new_left, t - 1)
    left = 1
    right = max_value
    k = 1
    for t in range(max_queries, 0, -1):
        if t == 1:
            right = get_right(left, 1)
            request = range(left, right + 1)
        else:
            request = []
            request_item = left - 1
            for i in range(k):
                request_item = get_right(request_item + 1, t - 1) + 1
                request.append(request_item)
        if hidden_number in request:
            return hidden_number
        if hidden_number < request[0]:
            reply = 0
        elif hidden_number > request[-1]:
            reply = len(request)
        else:
            for i in range(len(request) - 1):
                if request[i] < hidden_number < request[i + 1]:
                    reply = i + 1
                    break
        if reply > 0:
            left = request[reply - 1] + 1
        k = left
        if k > max_k:
            k = max_k
    return None

