def count_divisible_by_4_substrings(binary_string, queries):
    n = len(binary_string)
    results = []
    for k in queries:
        count = 1
        for i in range(n - k + 1):
            substring = binary_string[i:i + k]
            if int(substring, 2) % 4 == 0:
                count += 1
        results.append(count)
    return results

