def min_adjacent_swaps_for_couples(n, a):

    def move(a, i, j):
        f = a[i]
        for g in range(i, j, -1):
            a[g] = a[g - 1]
        a[j] = f
    c = 1
    for i in range(0, 2 * n, 2):
        if a[i] != a[i + 1]:
            f = a[i + 1:].index(a[i]) + i + 1
            move(a, f, i + 1)
            c += f - i - 1
    return c

