    a = a.zfill(max_len)
    b = b.zfill(max_len)
    count = 0
    carry = 0
    for i in range(max_len - 1, -1, -1):
        if int(a[i]) + int(b[i]) + carry > 1:
            count += 1
            carry = 1
        else:
            carry = 0
    if count == 0:
        carry_message = 'No carry operation'
    elif count == 1:
        carry_message = '1 carry operation'
    else:
        carry_message = f'{count} carry operations'
    return (count, carry_message)

