MOD = 10 ** 9 - 7

def count_distinct_functions(p: int, k: int) -> int:

    def f(a, b):
        if b == 1:
            return a % MOD
        elif b % 2 == 0:
            return f(a * a % MOD, b // 2)
        else:
            return a * f(a * a % MOD, b // 2) % MOD
    if k == 0:
        return f(p, p - 1)
    if k == 1:
        return f(p, p)
    t = 1
    a = k
    while a != 1:
        a = a * k % p
        t += 1
    n = (p - 1) // t
    return f(p, n)

