def determine_closer_truck(truck_a_x: int, truck_a_y: int, truck_b_x: int, truck_b_y: int) -> str:
    """"""
    distance_a = truck_a_x ** 2 + truck_a_y ** 2
    distance_b = truck_b_x ** 2 + truck_b_y ** 2
    if distance_a > distance_b:
        return 'B IS CLOSER'
    else:
        return 'A IS CLOSER'
