def connect_servers(n, m, v):
    if m < n - 1:
        return -1
    elif m > (n - 1) * (n - 2) / 2 + 1:
        return -1
    connections = []
    v = v % n
    connections.append(((v - 1) % n + 1, v + 1))
    for i in range(1, n - 1):
        connections.append(((v + i) % n + 1, (v + i + 1) % n + 1))
    m += n - 1
    i = 1
    while m > 0:
        j = 2
        while m > 0 and j < n - i + 1:
            if (v + i + j) % n != v:
                connections.append(((v + i) % n + 1, (v + i + j) % n + 1))
                m -= 1
            j += 1
        i += 1
    return connections

