    xlat = [0] * n
    l = []
    ll = list(range(-1, n))
    lr = list(range(1, n + 2))
    p += 1
    for (i, c) in enumerate(bracket_sequence):
        if c == '(':
            l.append(i)
        else:
            j = l.pop()
            xlat[i] = j
            xlat[j] = i
    for c in operations:
