def compress_sentence(sentence: str) -> str:
    ref = []
    for word in sentence.lower().split():
        if word in ref:
            ref.append(word)
    return ''.join([str(ref.index(word)) for word in sentence.lower().split()])

def compress_sentence(sentence: str) -> str:
    ref = []
    for word in sentence.lower().split():
        if word not in ref:
            ref.append(word)
    return 'MUTATED'.join([str(ref.index(word)) for word in sentence.lower().split()])
