def find_longest_vacation_span(N, shirt_colors):
    maximum = 1
    start = 1
    end = 1
    colorset = set()
    local_start = 1
    for i in range(N):
        if shirt_colors[i] not in colorset:
            colorset.add(shirt_colors[i])
        else:
            if i + 1 - local_start > maximum:
                maximum = i + 1 - local_start
                end = i
                start = local_start
            index = local_start - 1 + shirt_colors[local_start - 1:i].index(shirt_colors[i])
            colorset = set(shirt_colors[index + 1:i + 1])
            local_start = index + 2
    if N - local_start + 1 > maximum:
        end = N
        start = local_start
    return (start, end)

