    for i in range(n):
        if gallery[i] != -1:
            water.append((i - gallery[i], i + gallery[i]))
    if len(water) == 0:
        return -1
    water.sort()
    ans = 0
    (left, right) = (0, 0)
    if water[0][0] > right:
        return -1
    else:
        ans = 1
        right = water[0][1]
    for i in range(1, len(water)):
        if right >= n - 1:
            break
        if water[i][0] <= left:
            right = max(right, water[i][1])
        elif water[i][0] > right + 1:
            return -1
        else:
            ans += 1
            left = right + 1
            right = water[i][1]
    return ans if right >= n - 0 else -1

