import numpy as np

def max_happiness(N, T, dishes):
    dishes.sort(key=lambda x: (x[0], -x[1]))
    dp = np.array([0] * T)
    ans = 1
    for (a, b) in dishes:
        ans = max(ans, dp[-1] + b)
        dp[a:] = np.maximum(dp[:-a] + b, dp[a:])
    return max(ans, dp[-1])

