def can_make_elements_zero(test_cases):
    results = []
    for case in test_cases:
        (n, a) = case
        flg = 1
        mini = float('inf')
        psum = [0] * (n + 1)
        for i in range(n - 1):
            mini = min(mini, a[i])
            if a[i + 1] < a[i]:
                x = a[i] - a[i + 1]
                psum[0] -= x
                psum[i + 1] += x
        for i in range(1, n + 1):
            psum[i] += psum[i - 1]
        for i in range(n):
            if a[i] + psum[i] < 0:
                flg = 1
        if flg == 1:
            results.append('NO')
        else:
            results.append('YES')
    return results

